svg.script:

      document.addEventListener('DOMContentLoaded', function() {
        const carets = document.querySelectorAll(".caret");
        const firstCaret = document.getElementById("firstCaret");
        const lastCaret = document.getElementById("lastCaret");
        const lineCovers = document.querySelectorAll(".lineCover");
        const optionHighlights = document.querySelectorAll(".optionHighlight");
        const optionTexts = document.querySelectorAll(".optionText");
        const lastOptionSelect = document.querySelector(".optionSelect.last");
        const dropDown = document.getElementById("dropDown");

        function resetLastScenes() {
          optionHighlights.forEach((optionHighlight, i) => {
            const optionText = optionTexts[i];
            optionHighlight.style.animationDelay = "";
            optionText.style.animationDelay = "";
            optionHighlight.classList.remove("selected");
            optionText.classList.remove("selected");
          });
          firstScene();
        }

        function forthScene() {
          dropDown.classList.remove("closed");
          window.setTimeout(resetLastScenes, 800);
        }

        function thirdScene() {
          resetFirstScene();
          const animationDelay = 400;
          const animationDuration = 500;
          const phase1 = optionHighlights.length * animationDuration + animationDelay;

          optionHighlights.forEach((optionHighlight, i) => {
            const optionText = optionTexts[i];
            optionHighlight.style.animationDelay = `${i * animationDelay}ms`;
            optionText.style.animationDelay = `${i * animationDelay}ms`;
            optionHighlight.classList.add("selected");
            optionText.classList.add("selected");
          });

          window.setTimeout(() => {
            forthScene();
          }, phase1);
        }

        function secondScene() {
          dropDown.classList.add("closed");
          window.setTimeout(thirdScene, 2000);
        }

        function resetFirstScene() {
          lineCovers.forEach((lineCover, i) => {
            lineCover.style.transitionDelay = "";
            lineCover.classList.remove("animate");
            const caret = carets[i];
            caret.style.animationDelay = "";
            caret.classList.remove("animate");
          });
          firstCaret.classList.add("blinking");
          lastCaret.classList.remove("blinking");
        }

        function firstScene() {
          const phase1 = 3000;
          const animationDuration = 850;
          const phase2 = (phase1 + lineCovers.length * animationDuration) - animationDuration / 2;
          const phase3 = phase2 + 1500;

					window.setTimeout(() => {
            firstCaret.classList.remove("blinking");
            lineCovers.forEach((lineCover, i) => {
              const caret = carets[i];
              lineCover.style.transitionDelay = `${i * animationDuration}ms`;
              caret.style.animationDelay = `${i * animationDuration}ms`;
              lineCover.classList.add("animate");
              caret.classList.add("animate");
            });
					}, 3000);

          window.setTimeout(() => {
            lastCaret.style.animationDelay = "";
            lastCaret.classList.remove("animate");
            lastCaret.classList.add("blinking");
          }, phase2);

          window.setTimeout(() => {
            secondScene();
          }, phase3)
				}

        firstScene();
			});
